package NumberFunctions;

public class PrintEvenOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i<=100)
		{
			System.out.println("Odd Numbers are: "+i);
			i=i+2;
		}
		
		int j=2;
		while(j<=100) {
			System.out.println("Print Even Numbers: "+j);
			j=j+2;
		}

	}
	

}
